package ksvp;

import java.util.Scanner;

public class factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int n,i,a=1;
  Scanner scan= new Scanner(System.in);
  n = scan.nextInt();
  for(i=1;i<=n;i++){
	a=a*i;}
		
	System.out.println(a);
  }}


